import React, { useState } from "react";
import axios from "axios";

function UploadResume() {
  const [file, setFile] = useState(null);
  const [parsedData, setParsedData] = useState(null);
  const [jobDescription, setJobDescription] = useState("");
  const [atsScore, setAtsScore] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setParsedData(null);
    setAtsScore(null);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append("resume", file);

    try {
      const response = await axios.post("http://localhost:5000/upload", formData);
      setParsedData(response.data);
    } catch (err) {
      console.error("Upload Error:", err);
    }
  };

  const handleCalculateATS = async () => {
    if (!parsedData) return;
    try {
      const response = await axios.post("http://localhost:5000/ats", {
        resumeText: parsedData.text,
        jobText: jobDescription,
      });
      setAtsScore(response.data.score);
    } catch (err) {
      console.error("ATS Error:", err);
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif", maxWidth: "700px", margin: "auto" }}>
      <h2>ðŸ“„ Resume Analyzer</h2>
      
      <input type="file" accept=".pdf" onChange={handleFileChange} />
      <button onClick={handleUpload} style={{ marginLeft: "10px", padding: "5px 10px" }}>
        Upload
      </button>

      {parsedData && (
        <div style={{ marginTop: "20px", padding: "20px", border: "1px solid #ccc", borderRadius: "10px", backgroundColor: "#f9f9f9" }}>
          <h3>ðŸ‘¤ Resume Summary</h3>
          <p><strong>Name:</strong> {parsedData.name || "N/A"}</p>
          <p><strong>Email:</strong> {parsedData.email || "N/A"}</p>
          <p><strong>Phone:</strong> {parsedData.phone || "N/A"}</p>
        </div>
      )}

      <div style={{ marginTop: "30px" }}>
        <h3>ðŸ§  Job Description</h3>
        <textarea
          value={jobDescription}
          onChange={(e) => setJobDescription(e.target.value)}
          rows="6"
          placeholder="Paste job description here..."
          style={{ width: "100%", padding: "10px", borderRadius: "5px" }}
        ></textarea>
        <button onClick={handleCalculateATS} style={{ marginTop: "10px", padding: "8px 16px" }}>
          Calculate ATS Score
        </button>
      </div>

      {atsScore !== null && (
        <div style={{ marginTop: "20px" }}>
          <h3>ðŸ“Š ATS Score</h3>
          <div
            style={{
              backgroundColor: "#e0e0e0",
              borderRadius: "8px",
              overflow: "hidden",
              height: "30px",
              width: "100%",
            }}
          >
            <div
              style={{
                width: `${atsScore}%`,
                height: "100%",
                backgroundColor:
                  atsScore >= 70 ? "#4CAF50" : atsScore >= 40 ? "#FFC107" : "#F44336",
                textAlign: "center",
                color: "white",
                lineHeight: "30px",
                fontWeight: "bold",
              }}
            >
              {atsScore}%
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default UploadResume;
