// InterviewPractice.js
import React, { useState } from 'react';
import { getInterviewQA } from './api/interviewQuestions';
import { useLocation } from 'react-router-dom';

const InterviewPractice = () => {
  const location = useLocation();
  const { jobTitle, keywords } = location.state;

  const [level, setLevel] = useState('Beginner');
  const [qa, setQa] = useState('');
  const [loading, setLoading] = useState(false);

  const handleGenerate = async () => {
    setLoading(true);
    const result = await getInterviewQA(jobTitle, keywords, level);
    setQa(result);
    setLoading(false);
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Interview Practice for {jobTitle}</h2>

      <label className="block mb-2">Select Difficulty:</label>
      <select
        value={level}
        onChange={(e) => setLevel(e.target.value)}
        className="border p-2 mb-4 rounded"
      >
        <option value="Beginner">Beginner</option>
        <option value="Intermediate">Intermediate</option>
        <option value="Hard">Hard</option>
      </select>

      <button
        onClick={handleGenerate}
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        Generate Questions
      </button>

      {loading && <p className="mt-4 text-gray-500">Generating questions...</p>}

      {qa && (
        <div className="mt-6 whitespace-pre-wrap bg-gray-100 p-4 rounded shadow">
          {qa}
        </div>
      )}
    </div>
  );
};

export default InterviewPractice;
