import React, { useEffect, useState } from "react";
import axios from "axios";
import InterviewQA from "./InterviewQA";

function JobSuggestions({ keywords = [], summary = "" }) {
  const [jobs, setJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);
  const [stage, setStage] = useState("beginner");
  const [questionsData, setQuestionsData] = useState([]);
  const [loadingQuestions, setLoadingQuestions] = useState(false);

  const fetchQuestions = async (jobTitle, level) => {
    setLoadingQuestions(true);
    try {
      const res = await axios.post("http://localhost:5000/api/interview-qa", {
        role: jobTitle,
        level: level,
      });
      setQuestionsData(res.data.questions);
    } catch (err) {
      console.error("Failed to load interview questions", err);
    } finally {
      setLoadingQuestions(false);
    }
  };

  const handlePracticeClick = (jobTitle) => {
    setSelectedJob(jobTitle);
    fetchQuestions(jobTitle, stage);
  };

  return (
    <div style={{ marginTop: "20px", backgroundColor: "#fff", padding: "20px", borderRadius: "10px", boxShadow: "0 0 10px #eee" }}>
      <h2>ðŸ”¥ Job Suggestions</h2>
      {jobs.length > 0 ? (
        jobs.map((job, index) => (
          <div key={index} style={{ marginBottom: "20px", borderBottom: "1px solid #ddd", paddingBottom: "15px" }}>
            <strong>{job.job_title}</strong><br />
            {job.employer_name} â€“ {job.job_city}, {job.job_country}<br />
            <a href={job.job_apply_link} target="_blank" rel="noopener noreferrer">Apply Now</a>
            <br /><br />
            <label>Select Level: </label>
            <select value={stage} onChange={(e) => setStage(e.target.value)} style={{ padding: "5px", borderRadius: "5px" }}>
              <option value="beginner">Beginner</option>
              <option value="intermediate">Intermediate</option>
              <option value="advanced">Advanced</option>
            </select>
            <button onClick={() => handlePracticeClick(job.job_title)} style={{ marginLeft: "10px", padding: "5px 10px", borderRadius: "5px", background: "#007bff", color: "#fff", border: "none" }}>
              Practice Now
            </button>
          </div>
        ))
      ) : (
        <p>No job matches found based on your resume keywords.</p>
      )}

      {loadingQuestions ? <p>Loading questions...</p> : selectedJob && <InterviewQA questionsData={questionsData} />}
    </div>
  );
}

export default JobSuggestions;
