import React, { useState } from "react";
import axios from "axios";

function UploadResume() {
  const [file, setFile] = useState(null);
  const [parsedData, setParsedData] = useState(null);
  const [jobDescription, setJobDescription] = useState("");
  const [atsScore, setAtsScore] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append("file", file);
    const response = await axios.post("http://localhost:5000/upload", formData);
    setParsedData(response.data);
  };

  const calculateScore = async () => {
    const response = await axios.post("http://localhost:5000/ats-score", {
      resume_text: parsedData.text,
      job_description: jobDescription,
    });
    setAtsScore(response.data.score);
  };

  return (
    <div>
      <h2>Upload Resume</h2>
      <input type="file" onChange={handleFileChange} accept=".pdf" />
      <button onClick={handleUpload}>Upload</button>

      {parsedData && (
        <>
          <h3>Parsed Data</h3>
          <pre>{JSON.stringify(parsedData, null, 2)}</pre>

          <textarea
            placeholder="Paste Job Description Here..."
            value={jobDescription}
            onChange={(e) => setJobDescription(e.target.value)}
            rows="6"
            cols="60"
          ></textarea>
          <br />
          <button onClick={calculateScore}>Calculate ATS Score</button>
        </>
      )}

      {atsScore !== null && (
        <div>
          <h3>ATS Score: {atsScore}%</h3>
          <div style={{ backgroundColor: "#ddd", width: "300px", margin: "auto", borderRadius: "10px" }}>
            <div
              style={{
                width: `${atsScore}%`,
                backgroundColor: atsScore > 60 ? "green" : atsScore > 30 ? "orange" : "red",
                padding: "10px 0",
                borderRadius: "10px",
                color: "white",
                fontWeight: "bold",
              }}
            >
              {atsScore}%
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default UploadResume;
