import React, { useState } from "react";
import axios from "axios";

function InterviewQAGenerator() {
  const [jobRole, setJobRole] = useState("");
  const [level, setLevel] = useState("Beginner");
  const [qaList, setQaList] = useState([]);
  const [showAnswers, setShowAnswers] = useState({});
  const [loading, setLoading] = useState(false);

  const fetchQuestions = async () => {
    if (!jobRole) return alert("Please enter a job role.");

    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/api/interview-qa", {
        jobRole,
        level
      });
      setQaList(res.data.qa || []);
    } catch (err) {
      console.error("Failed to fetch questions", err);
      alert("Failed to generate interview questions.");
    } finally {
      setLoading(false);
    }
  };

  const toggleAnswer = (index) => {
    setShowAnswers((prev) => ({ ...prev, [index]: !prev[index] }));
  };

  return (
    <div className="qa-container" style={{ padding: "20px", background: "#f9f9f9", borderRadius: "10px" }}>
      <h2>ðŸ§  Practice Interview Q&A</h2>

      <div style={{ marginBottom: "15px" }}>
        <input
          type="text"
          placeholder="Enter job role (e.g. Data Scientist)"
          value={jobRole}
          onChange={(e) => setJobRole(e.target.value)}
          style={{ padding: "10px", marginRight: "10px" }}
        />
        <select value={level} onChange={(e) => setLevel(e.target.value)} style={{ padding: "10px" }}>
          <option>Beginner</option>
          <option>Intermediate</option>
          <option>Advanced</option>
        </select>
        <button onClick={fetchQuestions} style={{ marginLeft: "10px", padding: "10px 20px" }}>
          Generate
        </button>
      </div>

      {loading && <p>Loading questions...</p>}

      {qaList.map((item, index) => (
        <div key={index} style={{ marginBottom: "15px", borderBottom: "1px solid #ddd", paddingBottom: "10px" }}>
          <strong>Q{index + 1}: {item.question}</strong>
          <br />
          <button onClick={() => toggleAnswer(index)} style={{ marginTop: "5px" }}>
            {showAnswers[index] ? "Hide Answer" : "Show Answer"}
          </button>
          {showAnswers[index] && (
            <p style={{ marginTop: "10px" }}><strong>Answer:</strong> {item.answer}</p>
          )}
        </div>
      ))}
    </div>
  );
}

export default InterviewQAGenerator;
