import React, { useState } from "react";
import axios from "axios";

function JobSuggestions() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedJob, setSelectedJob] = useState(null);
  const [difficulty, setDifficulty] = useState("Beginner");
  const [questions, setQuestions] = useState([]);

  // Fetch jobs logic here...

  const handlePracticeClick = async (job) => {
    setSelectedJob(job);
    await fetchInterviewQuestions(job.job_title, difficulty);
  };

  const fetchInterviewQuestions = async (jobTitle, difficulty) => {
    try {
      const response = await axios.post("http://localhost:5000/generate-questions", {
        jobTitle,
        difficulty,
      });
      setQuestions(response.data.questions);
    } catch (err) {
      console.error("Error fetching questions:", err);
    }
  };

  return (
    <div>
      <h2>üî• Job Suggestions</h2>
      {loading ? (
        <p>Loading job listings...</p>
      ) : (
        <ul>
          {jobs.map((job, index) => (
            <li key={index}>
              <strong>{job.job_title}</strong> at <em>{job.employer_name}</em>
              <br />
              üìç {job.job_city}, {job.job_country}
              <br />
              <a href={job.job_apply_link} target="_blank" rel="noopener noreferrer">
                Apply Now
              </a>
              <br />
              <label>
                Difficulty Level:
                <select
                  value={difficulty}
                  onChange={(e) => setDifficulty(e.target.value)}
                >
                  <option value="Beginner">Beginner</option>
                  <option value="Intermediate">Intermediate</option>
                  <option value="Advanced">Advanced</option>
                </select>
              </label>
              <button onClick={() => handlePracticeClick(job)}>Practice Now</button>
            </li>
          ))}
        </ul>
      )}
      {selectedJob && (
        <div>
          <h3>Practice Questions for {selectedJob.job_title} - {difficulty} Level</h3>
          <ul>
            {questions.map((question, idx) => (
              <li key={idx}>{question}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default JobSuggestions;
