// components/JobSuggestions.js
import React, { useEffect, useState } from "react";
import axios from "axios";

function JobSuggestions() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const options = {
          method: 'GET',
          url: 'https://jsearch.p.rapidapi.com/search',
          params: {
            query: 'software developer',
            page: '1',
            num_pages: '1'
          },
          headers: {
            'X-RapidAPI-Key': '5f0a45efc5msha459b3624abbf5fp178efejsn4ade39898509',
            'X-RapidAPI-Host': 'jsearch.p.rapidapi.com'
          }
        };
        const res = await axios.request(options);
        setJobs(res.data.data || []);
        setLoading(false);
      } catch (err) {
        console.error("Failed to fetch jobs:", err);
        setLoading(false);
      }
    };

    fetchJobs();
  }, []);

  return (
    <div style={{ marginTop: "20px", backgroundColor: "#fff", padding: "20px", borderRadius: "10px", boxShadow: "0 0 10px #eee" }}>
      <h2>ðŸ”¥ Job Suggestions</h2>
      {loading ? (
        <p>Loading job listings...</p>
      ) : jobs.length > 0 ? (
        <ul style={{ listStyleType: "none", padding: 0 }}>
          {jobs.map((job, index) => (
            <li key={index} style={{ marginBottom: "15px", paddingBottom: "10px", borderBottom: "1px solid #ddd" }}>
              <h4 style={{ margin: "5px 0" }}>{job.job_title}</h4>
              <p style={{ margin: 0 }}><strong>Company:</strong> {job.employer_name}</p>
              <p style={{ margin: 0 }}><strong>Location:</strong> {job.job_city}, {job.job_country}</p>
              <a href={job.job_apply_link} target="_blank" rel="noopener noreferrer" style={{ color: "#007bff", textDecoration: "underline" }}>
                Apply Now
              </a>
            </li>
          ))}
        </ul>
      ) : (
        <p>No jobs found.</p>
      )}
    </div>
  );
}

export default JobSuggestions;
