// components/JobSuggestions.js
import React, { useEffect, useState } from "react";
import axios from "axios";

function JobSuggestions() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const options = {
          method: 'GET',
          url: 'https://jsearch.p.rapidapi.com/search',
          params: {
            query: 'software developer',
            page: '1',
            num_pages: '1'
          },
          headers: {
            'X-RapidAPI-Key': 'YOUR_API_KEY_HERE',
            'X-RapidAPI-Host': 'jsearch.p.rapidapi.com'
          }
        };
        const res = await axios.request(options);
        setJobs(res.data.data || []);
        setLoading(false);
      } catch (err) {
        console.error("Failed to fetch jobs:", err);
        setLoading(false);
      }
    };

    fetchJobs();
  }, []);

  return (
    <div style={{ marginTop: "20px", backgroundColor: "#fff", padding: "20px", borderRadius: "10px", boxShadow: "0 0 10px #eee" }}>
      <h2>üî• Job Suggestions</h2>
      {loading ? (
        <p>Loading job listings...</p>
      ) : (
        <ul>
          {jobs.map((job, index) => (
            <li key={index} style={{ marginBottom: "10px" }}>
              <strong>{job.job_title}</strong> at <em>{job.employer_name}</em><br />
              üìç {job.job_city}, {job.job_country}<br />
              <a href={job.job_apply_link} target="_blank" rel="noopener noreferrer">Apply Now</a>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default JobSuggestions;
